function selector(text, opts) {
  return opts[text] ?? "User input";
}

function pregunta1(text) {
  return selector(text, {
    "Excel·lent / Excelente: (Ha respost de manera amable, sense faltes, la quantitat de paraules fetes servir eren les necessàries, no ha parlat de més. / Ha respondido de manera amable, sin faltas, la cantidad de palabras usadas eran las necesarias, no habló de más.)": 5,
    "Bona / Buena: (Ha respost de manera correcta, amb poques faltes, la quantitat de paraules era apropiada. / Ha respondido de manera correcta, con pocas faltas, la cantidad de palabras era apropiada.)": 4,
    "Regular: (Ha respost de manera acceptable, però amb algunes faltes i paraules innecessàries. / Ha respondido de manera aceptable, pero con algunas faltas y palabras innecesarias.):": 3,
    "Dolenta / Mala: (Ha respost amb moltes faltes, i la quantitat de paraules era excessiva o molt baixa. / Ha respondido con muchas faltas, y la cantidad de palabras era excesiva o muy baja.)": 2,
    "Molt Dolenta / Muy Mala: (La resposta no era comprensible o no era adequada, moltes faltes, massa paraules o massa poques. / La respuesta no era comprensible o no era adecuada, muchas faltas, demasiadas palabras o muy pocas.)": 1
  });
}

function pregunta2(text) {
  return selector(text, {
    "Sí, molt informativa / Sí, muy informativa: (Les respostes eren clares i detallades, proporcionant tota la informació necessària. / Las respuestas eran claras y detalladas, proporcionando toda la información necesaria.)": 4,
    "Sí, informativa: (Les respostes eren útils, però podrien ser més detallades. / Las respuestas eran útiles, pero podrían ser más detalladas.)": 3,
    "Poc informativa / Poco informativa: (Les respostes eren vagues i no proporcionaven tota la informació necessària. / Las respuestas eran vagas y no proporcionaban toda la información necesaria.)": 2,
    "Gens informativa / Nada informativa: (Les respostes no eren útils o no proporcionaven informació rellevant. / Las respuestas no eran útiles o no proporcionaban información relevante.)": 1,
  });
}

function count_for(text, lista, listb) {
  var count = 0;

  for(var i = 0; i < lista.length; i++) {
    var key = lista[i];
    var value = listb[i];

    if(key != text) continue;

    var value_num = parseInt(value);

    if(isNaN(value_num)) continue;

    if(value_num < 0) continue;

    count += value_num;
  }

  return count;
}

function count_for_over_n_avg(text, lista, listb) {
  var count = 0;

  var n = 0;

  for(var i = 0; i < lista.length; i++) {
    var key = lista[i];
    var value = listb[i];

    if(key != text) continue;

    var value_num = parseInt(value);

    if(isNaN(value_num)) continue;

    if(value_num < 0) continue;

    count += value_num;
    n++;
  }

  return count / n;
}
